import React from 'react'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'

const Wrapper = styled.div`
 position: fixed;
 top: 0;
 left: 0;
 right: 0;
 bottom: 0;
 background: rgba(0, 0, 0, 0.6);
 display: flex; 
 align-items: center; 
 justify-content: center;
 color: white;
 /* background-color: white; */

`

const Modal = ({modalMovie, setOpenModal}) => {
 const {title, cast, rating, year, length, plot, rating_votes, poster, trailer} = modalMovie
 const renderCast = () => {
  console.log(cast)
  let i = 3
  while(i > 0){
   return <p>{cast.actor}</p>
  }
 }
 return (
  <Wrapper>
   <button onClick={() => setOpenModal(false)} className="close-btn">
    <FontAwesomeIcon icon={faTimes} />
   </button>
   <div className="movie-details">
    <h1>{modalMovie.title}</h1>
    <p>{rating}<span> rating</span></p>
    <p>{rating_votes} votes</p>
   </div>

   <div className="cast">
    {renderCast()}
   </div>
  </Wrapper>
 )
}

export default Modal
