import React from 'react'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import {colors} from '../utils/_var'

const Wrapper = styled.div`
 position: fixed;
 top: 0;
 left: 0;
 right: 0;
 bottom: 0;
 background: rgba(0, 0, 0, 0.6);
 display: flex; 
 align-items: center; 
 justify-content: center;
 color: white;
 flex-direction: column;
 .movie-title{

 }

 .close-btn{
  position: relative;
  bottom: 20%;
  width: 12vw;
  height: 10vh;
  font-size: 3rem;
  background: rgba(0, 0, 0, 0.1);
  color: white;
  border: none;
  cursor: pointer;
 }

 .close-btn:hover{
  color: ${colors.$highlight};

 }
 /* background-color: white; */

`

const Modal = ({modalMovie, setOpenModal}) => {
 const {title, cast, rating, year, length, plot, rating_votes, poster, trailer} = modalMovie

 const renderCast = () => {
  let castmem = []
  for(let i = 2; i >= 0; i--){
   castmem.push(cast[i].actor)
  }
  return castmem.map(actor => <p>{actor}</p>)
 }

 return (
  <Wrapper>

   <button onClick={() => setOpenModal(false)} className="close-btn">
    <FontAwesomeIcon icon={faTimes} />
   </button>

   <div className="movie-title">
    <h1>{title}</h1>
   </div>

   <div className="movie-details">
    <p>runtime: {length}</p>
    <h4>released: {year}</h4>
    <p> IMDB rating: <span> {rating}</span></p>
    <p>IMDB rating votes: <span>{rating_votes}</span></p>
   </div>

   <div className="cast">
    <h4>cast: </h4>
    {renderCast()}
   </div>

   <div className="plot">
    <p>{plot}</p>
   </div>
{ trailer ? 
   <div className="trailer-link">
    <a href={trailer.link} target="_blank" rel="noopener noreferrer">Check out a trailer</a>
   </div> : null}
  </Wrapper>
 )
}

export default Modal
