import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
import {useTransition, animated} from 'react-spring'

const Wrapper = styled.div`
 height: 100vh;
 width: 100vw;
 padding: 0;
 margin: 0;
 background-color: #C72C41;
 text-align: center;
`


const Container = styled.div`
 color: white;
 min-height: 200px;
 height: 60vh;
 width: 80vw;
 display: block;
 margin-left: auto;
 margin-right: auto;

 .item{
  font-size: 4rem;
  color: white;
  padding: 0;
  margin: 0;
 }
`




const Welcome = ({setWelcome}) => {
 const [items, setItems] = useState([])

 useEffect(() => {
  const timeout = setTimeout(() => setItems(v => v.length ? [] : [{y: 0, delay:200, text: 'Welcome'}, {y: 0, delay: 400, text: 'To' }, {y: 0, delay:600, text: 'The Movie Zone'}]), 2000)

  setTimeout(() => setWelcome(), 6500)

  return () => clearTimeout(timeout);
 })

 const transition = useTransition(items, {
  from: {x: -100, y: 800, opacity: 0, fontSize: '1rem'},
  enter: item => async (next) => {
   await next({y: item.y, opacity: 1, delay: item.delay});
   await next({x: 0, height: 100, width: 100, fontSize: '3rem'})
   await next({x: 0, height: 100, width: 100,opacity: 0.5})
   await next({x: 0, height: 100, width: 100,opacity: 0})
  },
  leave: {x: 100, y: 800, opacity: 0}
 });






 return (
  <Wrapper>
   <Container>
    {
     transition((style, item) => item ? <animated.h1 style={style}className="item">{item.text}</animated.h1> : '')}
   </Container>
  </Wrapper>
 )
}

export default Welcome
