import React, {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios'
import {colors} from '../utils/_var'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'



 const Wrapper = styled.div`
  color: white;
  .search-bar{
    width: 90vw;
    height: 5vh;
    border-radius: 10px;
    background-color: ${colors.$secondary};
    color: white;
    font-size: 3rem;
  }

  .search-btn{
    width: 20vw;
    height: 5vh;
    background-color: ${colors.$white};
  }
 `

const Search = ({configMovies}) => {
 const [title, setTitle] = useState('')


//  fetch movies from backend 
const movieFetch = (data) => {
 const movieQueries = []
 const uuid = localStorage.getItem('uuid')
  data.titles.map( query => movieQueries.push(query.id))
  const options = {
   method: 'GET',
   url: `http://localhost:3000/movies/`,
   headers: {'Content-Type': 'application/json'},
   params: {queries: movieQueries, uuid: uuid}
  }

  axios.request(options).then((resp) => 
    // sending both movies list to Home component to be combined
    configMovies(resp.data.movies, data.titles)
  ).catch((error) => console.log(error))
}




// fetch movies from rapid-api
 const handleForm = (e) => {
  e.preventDefault()
  const options = {
   method: 'GET',
   url: `${process.env.REACT_APP_API_BASE_URL}search/${title}`,
   headers: {
    'x-rapidapi-key': process.env.REACT_APP_RAPID_API_KEY,
    'x-rapidapi-host': process.env.REACT_APP_API_HOST
   }
  }

  axios.request(options).then(function (response) {
   movieFetch(response.data)
  }).catch(function (error) {
   console.error(error);
  });
 }






 return (
  <Wrapper>
   <form onSubmit={(event) => handleForm(event)}>
     <FontAwesomeIcon icon={faSearch} />
    <input 
        type="text" 
        name="movie-search" 
        id="movie-search" 
        placeholder="Search Movies"
        onChange={(event) => setTitle(event.target.value)}
      />
    <input type="submit" value="submit" className="search-btn"/>
   </form>
  </Wrapper>
 )
}

export default Search
