import React, {useState, useEffect} from 'react'
import { v4 } from 'uuid';
import axios from 'axios'
import styled from 'styled-components'
import {colors} from '../utils/_var'
import Header from './Header'
import Movies from './Movies'

const Wrapper = styled.div`
 background-color: ${colors.$black};
 display: flex;
 flex-direction: column;
 min-height: 100vh;
 padding: 0;
 margin: 0;
`



const Home = () => {
 const [uuid, setUuid] = useState('')
 const [movies, setMovies] = useState([])
 const [seenMovies, setSeenMovies] = useState([])
 const [rapidMovies, setRapidMovies] = useState([])

//  checking if user has a uuid in the localstorage, if not create an uuid and set to localStorage. 
 const checkLocalStorage = () => {
  let userCode = localStorage.getItem('uuid')
  if(userCode){
   setUuid(userCode)
  }else{
   userCode = v4()
   const options = {
    method: 'POST',
    url: 'http://localhost:3000/users',
    headers: {'Content-Type': 'application/json'},
    data: {uuid: userCode}
   }

   localStorage.setItem('uuid', userCode)

   // start loading animation here

   axios.request(options).then((response) => {
     console.log(response.data)
     // stop loading animation here
   }).catch((error) => console.log(error))
   setUuid(userCode)
  }
 }

 useEffect(() => {
  checkLocalStorage()
 })

 // add movies to the state
 const settingMoviesToState = (backendData, apiData) => {
   if(seenMovies.length){
      setSeenMovies([...seenMovies, backendData])
   }else{
     setSeenMovies(backendData)
   }
   setRapidMovies(apiData)
   console.log(rapidMovies)
   configMovies()
 }



// setting likes and dislikes from backend to each movie obj from rapid api
const configMovies = () => {
  let configedMovies = []
  console.log(rapidMovies)
  for(let i in rapidMovies){
    if(seenMovies.find(movie => movie['query'] === rapidMovies[i].id)){
      let backendMovie = seenMovies.find(movie => movie['query'] === rapidMovies[i].id)
      let tempObj = {...rapidMovies[i]}
      tempObj['up_count'] = backendMovie['up_count']
      tempObj['down_count'] = backendMovie['down_count']
      configedMovies.push(tempObj)
    }else{
      let tempObj = {...rapidMovies[i]}
      tempObj['up_count'] = 0
      tempObj['down_count'] = 0
      configedMovies.push(tempObj)
    }
  }
  setMovies(configedMovies)
}

const updateMovies = (backendMovies) => {
  settingMoviesToState(backendMovies, rapidMovies)
}




 return (
  <Wrapper>
    <Header configMovies={settingMoviesToState} />
    <Movies movies={movies} seenMovies={seenMovies} updateMovies={updateMovies}/>
  </Wrapper>
 )
}

export default Home
